import * as React from 'react';
import { Text, View, StyleSheet } from 'react-native';
import Constants from 'expo-constants';
import { Avatar } from 'react-native-elements';


export default function App() {
  return (
    <View style={styles.container}>
      <View style={styles.nameContainer}>
      <Avatar
          rounded
          title={nameToInitials(userToDisplay.name)}
          size="xlarge"
          source={{
              uri:
                  'https://s3.amazonaws.com/uifaces/faces/twitter/ladylexy/128.jpg',
          }}
        /> 
        <Text style={styles.heading}>
          {userToDisplay.name}, {userToDisplay.age}{userToDisplay.gender}
        </Text>
      </View>
      <View style={styles.descriptionContainer}>
        <Text style={styles.paragraph}>
          "{userToDisplay.description}"
        </Text>
        <View style={styles.columns}>
          <View style={styles.interestContainer}> 
            <Text style={styles.heading}>
              Categories
            </Text>
            <Text style={styles.paragraph}>
              {userToDisplay.interests}
            </Text>
          </View>
          <View style={styles.interestContainer}> 
            <Text style={styles.heading}>
              Interest
            </Text>
            <Text style={styles.paragraph}>
              {userToDisplay.interests}
            </Text>
          </View>
        </View>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    /* paddingTop: Constants.statusBarHeight, error with this being 0 right now*/
    paddingTop: 50,
    backgroundColor: '#ecf0f1',
    padding: 8,
  },
  columns: {
    flexDirection: 'row',
  },
  nameContainer: {
    flex: 2,
    justifyContent: 'flex-start',
    alignItems: 'center'
  },
  descriptionContainer:{
    flex: 3,
    flexDirection: "column",
    alignContent: 'flex-start',
    alignItems: 'center'
  },
  interestContainer: {

  },
  heading: {
    margin: 24,
    fontSize: 18,
    paddingHorizontal: 10,
    fontWeight: 'bold',
    textAlign: 'center',
  },
  paragraph: {
    margin: 24,
    fontSize: 18,
    paddingHorizontal: 10,
    textAlign: 'center',
  },
});

const userToDisplay = {
  name: 'Alex Kobuzovs',
  age: '21',
  gender: "M",
  university: 'University of Aberdeen',
  interests: ['computers', 'Windows', 'technical team'],
  id: 1,
  description: "Description here"
}

function nameToInitials(name) {
const words = name.split(' ')
return `${words[0][0]}${words[1][0]}`
}
