import * as React from 'react';
import { View, StyleSheet, Text } from 'react-native';
import Constants from 'expo-constants';
import SearchBar from '../components/SearchBar';
import RecommendationList from '../components/RecommendationList';

export default class Search extends React.Component {

  constructor(props) {
    super(props)
    this.state = {
      searchTerm: ''
    }
  }

  updateSearchTerm = (currentContent) => {
    this.setState({
      searchTerm: currentContent
    })
  }

  filterUsers = () => {
    if (this.state.searchTerm != '') {
      return users.filter((user) => {
        return user.interests.some((interest) => {
          return interest.toLowerCase().includes(this.state.searchTerm.toLowerCase())
        })
      })
    } else return users
  }

  render() {
    return <View style={styles.container}>
      <SearchBar updateSearchTerm={this.updateSearchTerm} />
      <RecommendationList users={this.filterUsers()} />
    </View>
  }
}

